import React from 'react'
import { Formik, Field, Form, ErrorMessage } from 'formik';

export const AuthFormCmp = ({ title, btnTxt, func, bottomLine }) => {

    // const dispatch = useDispatch()

    const initialValues = { email: '', password: '' }

    const validate = (values) => {
        console.log("MyForm ~ values", values)
        const errors = {};
        if (!values.email) {
            errors.email = 'Required';
        } else if (
            !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
        ) {
            errors.email = 'Invalid email address';
        }
        if (values.password.length < 8) {
            errors.password = 'too short pass'
        }
        else if (!/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{8,}$/.test(values.password)) {
            errors.password = 'the password should contain at least one number and one capital letter'
        }

        return errors;
    }

    // const onSubmit = (values, { setSubmitting }) => {
    //     console.log('submittes');
    //     dispatch(login(values))
    //     window.location.hash = '/info'
    // }

    return (
        <div className="form-container">
            <div className="main-title">Auth app</div>
            <div className="title">{title}</div>
            <Formik
                initialValues={initialValues}
                validate={validate}
                onSubmit={func}
            >
                <Form className="flex column">
                    <Field id="email" type="email" label="Email" name="email" placeholder="Email"/>
                    <ErrorMessage name="email" component="div" className="error email" />
                    <Field id="password" type="password" name="password" label="password" placeholder="Password"/>
                    <ErrorMessage name="password" component="div" className="error password" />
                    <button>{btnTxt}</button>
                </Form>
            </Formik>
            <span>{bottomLine}</span>


        </div>
    )
}

